mapper.select:
select

from
t_exam

    tid = #{tid}

        and `name` like concat('%',#{name},'%')


        and status = #{status}



            and ifnull(start_time,create_time) >= #{startTime}


            and ifnull(start_time,create_time) <= #{endTime}


            and ifnull(start_time,create_time) between #{startTime} and #{endTime}


            and ifnull(start_time,create_time) >= concat(year(now()) , '-' , month(now()) , '-' , '01' )

mapper.update:
update t_exam
set status = #{status}
where id = #{id}
